import React from 'react';
import logo from './logo.svg';
import './App.css';

function Square(props) {
  return (
    <button className="square" onClick={()=>props.onClick()}>
      {props.value}
    </button>
  )
}

class Board extends React.Component {  
  constructor(props) {
    super(props);
    this.state = {
      board: Array(9).fill(null),
      xTurn: true,
    }
    this.board = Array(9).fill(null);
    this.xTurn = true;
  }

  renderSquare(i) {
    return (
      <Square 
        value={this.state.board[i]} 
        onClick={() => this.doClick(i)}
      />
    );
  }

  postBoard(board = Array(9).fill(null)) {
    const requestContent = {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        board: this.board,
        xTurn: !this.xTurn,
      }),
    };

    fetch('/board', requestContent)
      .then((res) => res.json())
      .then((data) => console.log(data));
  }

  getBoard() {
    fetch('/board')
    .then((res) => res.json())
    .then((board) => this.updateBoard(board));
  }

  updateBoard(newBoard) {
    console.log(newBoard);
    this.board = newBoard;
  }

  doClick(i) {
    const squares = this.board.slice();
    squares[i] = this.xTurn ? 'X' : 'O';
    this.board = squares.slice();
  }

  render() {
    return (
      <div>
        <div className='board-row'>
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className='board-row'>
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className='board-row'>
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
        <button onClick={() => this.postBoard()}>Submit Move</button>
        <button onClick={() => this.getBoard()}>Refresh Board</button>
      </div>
    );
  }
}

